def get_decimal_by_symbol(symbol):
    decimals = {'USDC': 6, 'DAI': 18, 'ARB': 18, 'anyWETH': 18, 'WETH': 18, 'WBTC':18}
    return decimals[symbol]

def check_token_balance(web3, address_wallet, token_address, token_decimals):
    token_contract = web3.eth.contract(address=token_address, abi=Router.abi_token)
    balance = token_contract.functions.balanceOf(address_wallet).call()
    return balance / (10 ** token_decimals)  # Преобразование в читаемый формат


def swap_token_for_eth(private_key, token_address, token_symbol):
    web3 = Web3(Web3.HTTPProvider(RPC))
    account = Account.from_key(private_key)
    address_wallet = account.address
    decimal = get_decimal_by_symbol(token_symbol)

    token_contract = web3.eth.contract(address=token_address, abi=Router.abi_token)
    token_balance = token_contract.functions.balanceOf(address_wallet).call()

    # Если баланс 0, пропускаем своп
    if token_balance == 0:
        print(f"Недостаточно средств для свопа {token_symbol}")
        return

    amount_in_wei = token_balance
    slippage_amount = int(amount_in_wei * slippage)
    min_tokens_received = amount_in_wei - slippage_amount

    # Проверка и утверждение токенов
    allowance = token_contract.functions.allowance(address_wallet, Router.arb_router).call()
    if allowance < amount_in_wei:
        approve_tx = token_contract.functions.approve(Router.arb_router, amount_in_wei).build_transaction({
            'from': address_wallet,
            'gasPrice': web3.eth.gas_price,
            'nonce': web3.eth.get_transaction_count(address_wallet)
        })
        signed_approve_tx = web3.eth.account.sign_transaction(approve_tx, private_key)
        web3.eth.send_raw_transaction(signed_approve_tx.rawTransaction)
        web3.eth.wait_for_transaction_receipt(signed_approve_tx.hash)

    current_nonce = web3.eth.get_transaction_count(address_wallet)  # Получаем текущий nonce
    swap_contract = web3.eth.contract(address=Router.arb_router, abi=Router.abi_arb)
    swap_txn = swap_contract.functions.swapExactTokensForETH(
        amount_in_wei,
        min_tokens_received,
        [token_address, Router.eth],
        address_wallet,
        int(time.time()) + 100000
    ).build_transaction({
        'from': address_wallet,
        'gas': gas,
        'gasPrice': web3.eth.gas_price,
        'nonce': current_nonce,  # Используем полученный nonce
    })

    signed_txn = web3.eth.account.sign_transaction(swap_txn, private_key)
    tx_hash = web3.eth.send_raw_transaction(signed_txn.rawTransaction)

    print(f'>>> Sold {token_symbol} ArbSwap | https://nova.arbiscan.io/tx/{web3.to_hex(tx_hash)}')
    print(f'    Address: {address_wallet}')

def withdraw_weth(private_key, weth_contract_address, amount):
    web3 = Web3(Web3.HTTPProvider(RPC))
    account = Account.from_key(private_key)
    address_wallet = account.address

    # Используйте корректный ABI для WETH
    weth_contract = web3.eth.contract(address=weth_contract_address, abi=Router.abi_weth)

    withdraw_txn = weth_contract.functions.withdraw(amount).build_transaction({
        'from': address_wallet,
        'gas': gas,
        'gasPrice': web3.eth.gas_price,
        'nonce': web3.eth.get_transaction_count(address_wallet),
    })

    signed_txn = web3.eth.account.sign_transaction(withdraw_txn, private_key)
    tx_hash = web3.eth.send_raw_transaction(signed_txn.rawTransaction)

    print(f'>>> Withdraw WETH | https://nova.arbiscan.io/tx/{web3.to_hex(tx_hash)}')
    print(f'    Address: {address_wallet}')


def process_wallet(private_key, wallet_id):
    web3 = Web3(Web3.HTTPProvider(RPC))
    account = Account.from_key(private_key)
    address_wallet = account.address

    print(f"Кошелек {wallet_id}:")
    for token in token_arr:
        token_symbol = token['symbol']
        token_address = token['address']
        token_decimals = get_decimal_by_symbol(token_symbol)
        balance = check_token_balance(web3, address_wallet, token_address, token_decimals)

        if balance > 0:
            print(f"  Доступный баланс для {token_symbol}: {balance}")
            if token_symbol == 'WETH':
                amount_in_wei = int(balance * (10 ** token_decimals))
                withdraw_weth(private_key, token_address, amount_in_wei)
            else:
                swap_token_for_eth(private_key, token_address, token_symbol)
            time.sleep(random.uniform(*delay_txn))  # задержка после операции
        else:
            print(f"  Недостаточный баланс для {token_symbol}")

    time.sleep(random.uniform(*delay_acc))  # задержка после обработки кошелька

if __name__ == '__main__':
    with open("wallets.txt", "r") as f:
        keys_list = [row.strip() for row in f]
    print(f'Number of wallets: {len(keys_list)}')


    def main():
        while keys_list:
            private_key = keys_list.pop(0)
            web3 = Web3(Web3.HTTPProvider(RPC))
            account = Account.from_key(private_key)
            address_wallet = account.address

            for token in token_arr:
                token_symbol = token['symbol']
                token_address = token['address']
                token_decimals = get_decimal_by_symbol(token_symbol)
                balance = check_token_balance(web3, address_wallet, token_address, token_decimals)

                if balance > 0:
                    print(f"Доступный баланс для {token_symbol}: {balance}")
                    if token_symbol == 'WETH':
                        amount_in_wei = int(balance * (10 ** token_decimals))
                        withdraw_weth(private_key, token_address, amount_in_wei)
                    else:
                        swap_token_for_eth(private_key, token_address, token_symbol)
                    time.sleep(random.uniform(delay_txn[0], delay_txn[1]))
                else:
                    print(f"Недостаточный баланс для {token_symbol}")

            time.sleep(random.uniform(delay_acc[0], delay_acc[1]))


    for _ in range(number_of_threads):
        threading.Thread(target=main).start()